  return (
      <header className='header'>
        <h1 >{title}</h1>
        <Button color={'green'} text={
          showAdd?'Close':'Add'
        }
        onClick=
        {onAdd}/>
      </header>

  )
}

Header.defaultProps=
{
  title:'Task Tracker',
}

